(()=>{const e=document.querySelector("[data-sidebar]"),t=document.querySelectorAll("[data-sidebar-toggle]");if(!e||!t.length)return;const n=()=>{const t=e.getAttribute("data-open")==="true";e.setAttribute("data-open",String(!t))};t.forEach(e=>{e.addEventListener("click",n)}),window.matchMedia("(min-width: 961px)").addEventListener("change",t=>{t.matches?e.setAttribute("data-open","true"):e.setAttribute("data-open","false")}),window.matchMedia("(min-width: 961px)").matches&&e.setAttribute("data-open","true")})()